import {getDocs, collection} from 'firebase/firestore';
import { getDb } from './firebase';

const nome_tabela = 'campanha';

export const findAll = async () => {
    const doc_refs = await getDocs(collection(getDb(), nome_tabela));

    const res = [];

    doc_refs.forEach(camp => {
        res.push({
            id: camp.id,
            ...camp.data()
        });
    });

    return res;
}
