import React, { useState } from 'react';
import axios from 'axios';

function CreateCampaign() {
  const [campaignData, setCampaignData] = useState({
    name: '',
    goal: '',
    deadline: '',
    description: '',
    images: [],
    videos: [],
    documents: [],
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setCampaignData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleImageUpload = e => {
    const files = Array.from(e.target.files);
    setCampaignData(prevState => ({
      ...prevState,
      images: prevState.images.concat(files),
    }));
  };

  const handleVideoUpload = e => {
    const files = Array.from(e.target.files);
    setCampaignData(prevState => ({
      ...prevState,
      videos: prevState.videos.concat(files),
    }));
  };

  const handleDocumentUpload = e => {
    const files = Array.from(e.target.files);
    setCampaignData(prevState => ({
      ...prevState,
      documents: prevState.documents.concat(files),
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();

    try {
      const response = await axios.post(
        'URL_DA_SUA_API', // Substitua pela URL correta da sua API Firestore
        campaignData,
        { headers: { 'Content-Type': 'application/json' } } // Especifica o tipo de dados JSON
      );

      console.log('Resposta da API:', response.data);

      setCampaignData({
        name: '',
        goal: '',
        deadline: '',
        description: '',
        images: [],
        videos: [],
        documents: [],
      });
    } catch (error) {
      console.error('Erro ao criar campanha:', error);
    }
  };

  return (
    <div className="create-campaign" style={{ backgroundColor: 'yellow', padding: '20px' }}>
      <h2 style={{ color: 'black' }}>Criar Campanha</h2>
      <form onSubmit={handleSubmit}>
        {/* Restante do formulário */}
      </form>
    </div>
  );
}

export default CreateCampaign;
